import React, { useEffect,useState , useContext} from "react"
import { getNearByHotels} from "../../services/ViewPOIServices"
import { TravelContext } from "../../context/TravelContext"
import { PlaceCard } from "../../components/TravelPlanApp/placeCard"
import { Flex } from "@chakra-ui/layout"

export const NearByHotels=()=>{
    const [data,setData]=useState([])
    const {state, dispatch}=useContext(TravelContext)
 

    useEffect( ()=>{
            getNearByHotels(state.accomodation_location).then((r)=>{
                
                setData(r)
                console.log(r)
                
                
            }); 
            
        },[state.accomodation_location] )

  
    return(
        <>
        <Flex flexDirection="column" alignItems="center">
        {(data) && data.map((Item, index)=>{
            return(
                <>
                
                <PlaceCard  photo={Item.photos[0].photo_reference} index={index} name={Item.name} address={Item.formatted_address}  rating={Item.rating} place_id={Item.place_id}/>

                </>
            )
        })}
        
        </Flex>
        </>
    )
}
