import { useState, useRef, useEffect } from "react";
import { Flex, HStack, VStack } from "@chakra-ui/layout"
import { Text, Image, Heading , Button, AlertDialog, AlertDialogBody, AlertDialogFooter, AlertDialogHeader, AlertDialogContent, AlertDialogOverlay,} from "@chakra-ui/react"
import { AiOutlineDelete } from "react-icons/ai";

export const MyPlans=()=>{
    const [plans,setPlans]=useState([])

    const [isOpen, setIsOpen] = useState(false)
    const onClose = () => setIsOpen(false)
    const cancelRef = useRef()
    
   

    useEffect(()=>{

        fetch('/user/getTravelPlans').then(res=>res.json())
        .then(result=>{
            console.log(result)
            setPlans(result.myPlans)
        })

     },[])

    return(
        <>
                <AlertDialog
                isOpen={isOpen}
                leastDestructiveRef={cancelRef}
                onClose={onClose}
            >
                <AlertDialogOverlay>
                <AlertDialogContent>
                    <AlertDialogHeader fontSize="lg" fontWeight="bold">
                    Delete Travel Plan
                    </AlertDialogHeader>

                    <AlertDialogBody>
                    Are you sure? You can't undo this action afterwards.
                    </AlertDialogBody>

                    <AlertDialogFooter>
                    <Button ref={cancelRef} onClick={onClose}>
                        Cancel
                    </Button>
                    <Button colorScheme="red" onClick={onClose} ml={3}>
                        Delete
                    </Button>
                    </AlertDialogFooter>
                </AlertDialogContent>
                </AlertDialogOverlay>
            </AlertDialog>
        
            <>
            
            {plans.map((plan,index)=>{
                    return(
                        <>
                         <Flex flexDirection="column" alignItems="center" p={3}>
                            <HStack>

                            <Button colorScheme="red" onClick={(()=>{
                                setIsOpen(true)
                            })}><AiOutlineDelete /></Button>
                                  <PlanCard  name={plan.name ? plan.name : `My plan ${index+1}`} days={plan.travelPlan[0].length} createdDate={plan.createdAt}/>

                            </HStack>
                         </Flex>
                        </>
                    )
            })}
            
            
            </>


        </>
    )
}

const PlanCard=({name,days,createdDate})=>{
    return(
       
                    <Flex margin={5} borderWidth="1px" borderRadius="lg" p={5} maxWidth="container.md" >
                       <HStack>
                       <Image src={"https://images.unsplash.com/photo-1632429619634-3d97fc1693e9?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=387&q=80"}  width="400px" height="300px" objectFit="cover" borderRadius="lg"  /> 
                       <VStack p={3} alignItems="start" spacing={5}>
                                <Heading as="h3" size="lg">
                                    {name}
                                </Heading>

                                <Heading as="h5" size="sm">
                                    {days} days
                                </Heading>

                                <Button colorScheme="teal" variant="outline" onClick={()=>{
                                    
                                }}>
                                    View Plan
                                </Button>

                                <Text fontSize="sm">created : {}}</Text>
                       </VStack>
                       </HStack>

                    </Flex>
                    
    )
}
