import NavBar from "../components/navbar"
import { Select } from "@chakra-ui/select"
import { InputGroup } from "@chakra-ui/input"
import { Flex, HStack, Text } from "@chakra-ui/layout"
import { useEffect, useState,useRef } from "react"
import { BiSearch } from "react-icons/bi"
import { PlanCard } from "../components/AdminComponents/planCard"
import { AiOutlineDelete } from "react-icons/ai"
import { MdPublic } from "react-icons/md"
import { Button, AlertDialog, AlertDialogBody, AlertDialogFooter, AlertDialogHeader, AlertDialogContent, AlertDialogOverlay,} from "@chakra-ui/react"


export const SharedPlan=()=>{
    const [rate,setRate]=useState("")
    const [plans,setPlans]=useState([])
    const [pid,setPID]=useState("")

    // useEffect(()=>{
    //     getSharedPlans()
    // },[])
    const [isOpen, setIsOpen] = useState(false)
    const onClose = () => setIsOpen(false)
    const cancelRef = useRef()

    const [isOpen1, setIsOpen1] = useState(false)
    const onClose1 = () => setIsOpen1(false)
    const cancelRef1 = useRef()


    const getSharedPlans=()=>{
        fetch('/admin/getsharedPlans',{
            method:"post",
            headers:{
                "Content-type":"application/json"
            },
            body:JSON.stringify(
                {
                    rate:rate,

                }
            )
        }).then(res=>res.json()).
        then(data=>{
            setPlans(data.myPlans)
            console.log(data)
        }).catch(e=>{
            console.log(e)
        })
    }

    const deleteAleart=(planId)=>{
        console.log(planId)
        setIsOpen(true)
        setPID(planId)
    }
    const publicAleart=(planId)=>{
        console.log(planId)
        setIsOpen1(true)
        setPID(planId)
    }

    const deletePlan=()=>{
        fetch('/admin/deleteTravelPlan',{
            method:"delete",
            headers:{
                "Content-Type":"application/json",
            },
            body:JSON.stringify({
                planId:pid})
            }).then(res=>res.json()).
            then(result=>{
                if(result.data){
                    setIsOpen(false)
                    const newPlans=plans.filter(plan=>{
                        return plan._id !=result.data._id                            
                    })
                    setPlans(newPlans)
                }
                
                console.log(result)
            })
            .catch((e)=>{
                console.log(e)
            })
        
    }
    const publicPlan=()=>{
        fetch('/admin/setPublicPlan',{
            method:"delete",
            headers:{
                "Content-Type":"application/json",
            },
            body:JSON.stringify({
                planId:pid})
            }).then(res=>res.json()).
            then(result=>{
                if(result.data){
                    setIsOpen1(false)     
                }
                
                console.log(result)
            })
            .catch((e)=>{
                console.log(e)
            })
        
    }

    return(
        <>

        <AlertDialog
                isOpen={isOpen}
                leastDestructiveRef={cancelRef}
                onClose={onClose}
            >
                <AlertDialogOverlay>
                <AlertDialogContent>
                    <AlertDialogHeader fontSize="lg" fontWeight="bold">
                    Delete Travel Plan
                    </AlertDialogHeader>

                    <AlertDialogBody>
                    Are you sure? You can't undo this action afterwards.
                    </AlertDialogBody>

                    <AlertDialogFooter>
                    <Button ref={cancelRef} onClick={onClose}>
                        Cancel
                    </Button>
                    <Button colorScheme="red" onClick={()=>{
                        deletePlan();
                    }} ml={3}>
                        Delete
                    </Button>
                    </AlertDialogFooter>
                </AlertDialogContent>
                </AlertDialogOverlay>
        </AlertDialog>


        <AlertDialog
                isOpen={isOpen}
                leastDestructiveRef={cancelRef1}
                onClose={onClose1}
            >
                <AlertDialogOverlay>
                <AlertDialogContent>
                    <AlertDialogHeader fontSize="lg" fontWeight="bold">
                    Delete Travel Plan
                    </AlertDialogHeader>

                    <AlertDialogBody>
                    Are you sure? You can't undo this action afterwards.
                    </AlertDialogBody>

                    <AlertDialogFooter>
                    <Button ref={cancelRef} onClick={onClose1}>
                        Cancel
                    </Button>
                    <Button colorScheme="red" onClick={()=>{
                        publicPlan();
                    }} ml={3}>
                        Delete
                    </Button>
                    </AlertDialogFooter>
                </AlertDialogContent>
                </AlertDialogOverlay>
        </AlertDialog>
        
        <NavBar>
        <Flex flexDirection="column" alignItems="center">
        <HStack width="full">
        <InputGroup>
            <Text>select rate</Text>
            <Select defaultValue="5" onChange={(e)=>setRate(e.target.value)} value={rate}>
            <option value="5">5</option>
            <option value="4">4</option>
            <option value="3">3</option>
            <option value="2">2</option>
            <option value="1">1</option>
            </Select>
            </InputGroup>
            <Button colorScheme="teal" size="xs" leftIcon={<BiSearch/>} p={4} onClick={()=>getSharedPlans()}>
                search
            </Button>
        </HStack>
        </Flex>



        <>
        {plans && plans.map((plan,index)=>{
                    return(
                        <>
                         <Flex flexDirection="column" alignItems="center" p={3}>
                            <HStack>

                            <Button colorScheme="red" onClick={()=>{
                                         deleteAleart(plan._id)
                            }}><AiOutlineDelete /></Button>



                            <Button colorScheme="blue" onClick={()=>{
                                        publicAleart(plan._id)
                            }}><MdPublic /></Button>
                        
                            <PlanCard _id={plan._id} name={plan.name ? plan.name : `My plan ${index+1}`} days={plan.travelPlan[0].length} createdDate={plan.createdAt} travelPlan={plan.travelPlan} />

                            </HStack>
                         </Flex>
                        </>
                    )
        })}

        </>














        </NavBar>

        
        
        </>
    )
}
