import React, { useState, useEffect } from 'react'
import { Heading,Center, Flex,   } from '@chakra-ui/layout'
import { useParams } from 'react-router'
import { Description } from '../components/description'
import { Galary } from '../components/galary'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';


export const Province = (props) => {
  const {pid}=useParams()
  const [ima,setIma]=useState([])
  const [name, setName]=useState("")
  const [description, setDescription]=useState("")
  useEffect(()=>{
    
     fetch("/admin/getProvinceData/"+pid).
     then(res=>res.json())
     .then(data=>{
        console.log(data)
        setIma(data.images)
        setName(data.name)
        setDescription(data.description)
       
     }).catch(err=>{
         console.log(err)
     })
  
  },[])
          
  return (
   
     <Flex flexDirection="column" boxShadow="2xl" bg="white" p={10}>
     <Center><Heading mb={4}>{name}</Heading></Center>
     <Galary imarr={ima} pid={pid} />
     <Description des={description} pid={pid} />
     </Flex>
   
  )
}








const Carousal=({photos=[]})=>{
    const [references, setReferences]=useState([])
    useEffect(()=>{
            setReferences(photos)
    }, [photos])
    return(
        <Carousel>
                
                {
                    references.map((reference)=>{
                        return(
                            <div >
                            <img src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=800&photo_reference=${reference.photo_reference}&key=AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY`} 
                             />
                            
                        </div>
                        )
                    })
                }
                
            </Carousel>
    )
}
