import { Flex, HStack, VStack } from "@chakra-ui/layout"
import { Text, Image, Heading , Button, AlertDialog, AlertDialogBody, AlertDialogFooter, AlertDialogHeader, AlertDialogContent, AlertDialogOverlay,} from "@chakra-ui/react"
import { AiOutlineDelete } from "react-icons/ai";

export const MyPlans=()=>{
    const [plans,setPlans]=useState([])
    
    const [isOpen, setIsOpen] = useState(false)
    const onClose = () => setIsOpen(false)
    const cancelRef = useRef()
    
   

    useEffect(()=>{
        fetch('/user/getTravelPlans').then(res=>res.json())
        .then(result=>{
            console.log(result)
            // setPlans(result.myPlans)
        })
     },[])

    return(
        <>
                <AlertDialog
                isOpen={isOpen}
                leastDestructiveRef={cancelRef}
                onClose={onClose}
            >
                <AlertDialogOverlay>
                <AlertDialogContent>
                    <AlertDialogHeader fontSize="lg" fontWeight="bold">
                    Delete Travel Plan
                    </AlertDialogHeader>

                    <AlertDialogBody>
                    Are you sure? You can't undo this action afterwards.
                    </AlertDialogBody>

                    <AlertDialogFooter>
                    <Button ref={cancelRef} onClick={onClose}>
                        Cancel
                    </Button>
                    <Button colorScheme="red" onClick={onClose} ml={3}>
                        Delete
                    </Button>
                    </AlertDialogFooter>
                </AlertDialogContent>
                </AlertDialogOverlay>
            </AlertDialog>
        
            <>
            
            {/* {plans.map((plan)=>{
                    return(
                        <>
                         <Flex flexDirection="column" alignItems="center" p={3}>
                            <HStack>

                            <Button colorScheme="red" onClick={(()=>{
                                setIsOpen(true)
                            })}><AiOutlineDelete /></Button>
                                  <PlanCard />

                            </HStack>
                         </Flex>
                        </>
                    )
            })} */}
            
            
            </>


        </>
    )
}

