import React, { useState, useRef,useEffect } from 'react'
import 'photoswipe/dist/photoswipe.css'
import 'photoswipe/dist/default-skin/default-skin.css'
import M from 'materialize-css'
import { Textarea } from "@chakra-ui/react"
import PhotoswipeUIDefault from 'photoswipe/dist/photoswipe-ui-default'
import { CustomGallery, Item, DefaultLayout } from 'react-photoswipe-gallery'
import { Button } from '@chakra-ui/button'
import { Box, Flex, Heading,Center,   } from '@chakra-ui/layout'
import NavBar from '../components/navbar'
import { useParams } from 'react-router'
import { Description } from '../components/description'
import { Galary } from '../components/galary'


export const ViewProvince = (props) => {
  const {pid}=useParams()
  const [ima,setIma]=useState([])
  const [name, setName]=useState("")
  const [description, setDescription]=useState("")
  useEffect(()=>{
    
     fetch("/admin/getProvinceData/"+pid).
     then(res=>res.json())
     .then(data=>{
        console.log(data)
        setIma(data.images)
        setName(data.name)
        setDescription(data.description)
       
     }).catch(err=>{
         console.log(err)
     })
  
  },[])
          
  return (
   <NavBar>
     <>
     <Center><Heading mb={4}>{name}</Heading></Center>
     <Galary imarr={ima} pid={pid} />
     <Description des={description} pid={pid} />
     </>
   </NavBar>
  )
}
