import React, { useState, useRef } from 'react'
import 'photoswipe/dist/photoswipe.css'
import 'photoswipe/dist/default-skin/default-skin.css'

import PhotoswipeUIDefault from 'photoswipe/dist/photoswipe-ui-default'
import { CustomGallery, Item, DefaultLayout } from 'react-photoswipe-gallery'
import { Button } from '@chakra-ui/button'
import { Box, Flex } from '@chakra-ui/layout'
import NavBar from '../components/navbar'
import { DeleteIcon,ArrowUpIcon } from '@chakra-ui/icons'
export const TestPage = () => {
  

  return (
   <NavBar>
     <Galary/>
   </NavBar>
  )
}
const Galary=()=>{
  const layoutRef = useRef()
  const inputRef = useRef()
  const [images,setImages]=useState(["https://placekitten.com/1024/768?image=1","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2","https://placekitten.com/1024/768?image=2"])
  const [isLoading,setLoading]=useState(false)
  const [url,setUrl]=useState("")

  const postDetails = ()=>{
    const data = new FormData()
    data.append("file",image)
    data.append("upload_preset","new-insta")
    data.append("cloud_name","cnq")
    fetch("https://api.cloudinary.com/v1_1/cnq/image/upload",{
        method:"post",
        body:data
    })
    .then(res=>res.json())
    .then(data=>{
       setUrl(data.url)
    })
    .catch(err=>{
        console.log(err)
    })
  
  
  }
  useEffect(()=>{
    if(url){
     fetch("/admin/addImgtoProvince",{
         method:"post",
         headers:{
             "Content-Type":"application/json",
         },
         body:JSON.stringify({
             pic:url
         })
     }).then(res=>res.json())
     .then(data=>{
  
        if(data.error){
           M.toast({html: data.error,classes:"#c62828 red darken-3"})
        }
        else{
            M.toast({html:"Created post Successfully",classes:"#43a047 green darken-1"})
            history.push('/')
        }
     }).catch(err=>{
         console.log(err)
     })
  }
  },[url])










  return(
    <Flex flexDirection="column" alignItems="center">
    <Flex flexDirection="row" overflowX="scroll" margin="5">
    <CustomGallery layoutRef={layoutRef} ui={PhotoswipeUIDefault}>

      {images.map((image)=>{
         return(
          <Item
          original={image}
          thumbnail={image}
          width="1024"
          height="768"
        >
          {({ ref, open }) => (
           <>
            <img ref={ref} onClick={open} src={image}  width="200px" height="200px"/>
            <Button ><DeleteIcon/></Button>
           </>
          )}
        </Item>
         )
      })}
   
    </CustomGallery>

    <DefaultLayout
      shareButton={false}
      fullscreenButton={true}
      zoomButton={true}
      ref={layoutRef}
    />
       
    </Flex>
  
    <Button
    isLoading={isLoading}
    loadingText="Uploading"
    colorScheme="teal"
    variant="outline"
    rightIcon={<ArrowUpIcon/>}
    onClick={()=>inputRef.current.click()}
    onChange={()=>postDetails()}
    
  >
    Upload
  </Button>
  <input  ref={inputRef} type="file" style={{display:"none"}} onChange={(e)=>setImage(e.target.files[0])}/>
    </Flex>
    
  )
}
