import { Flex } from '@chakra-ui/layout';
import { withScriptjs, withGoogleMap, GoogleMap, Marker, InfoWindow } from "react-google-maps"
import {useState} from "react";
import Autocomplete from "react-google-autocomplete";


export const CustomPlan=()=>{
    const [place,setPlace]=useState({})
    return(
        <>
        <Flex  flexDirection="row">
            <Flex width="50%" flexDirection="column" justifyContent="center" alignItems="center">


            <Autocomplete
                apiKey={"AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY"}


                onPlaceSelected={(place) => {
                    console.log(place);
                    setPlace(place.geometry.location)
                }}

                options={{
                    componentRestrictions: { country: "LK" },
                  }}
                />
                                
            </Flex>

            
            <GMAP location={place}/>
            

        </Flex>
        
    
        
        </>
    )
}










export const Map=({location})=>{
  const [open,setOpen]=useState(false);

  
    return(
        <>

    <GoogleMap
      defaultZoom={8}
      defaultCenter={{ lat: 7.291418, lng:80.636696}}
      // defaultOptions={{ styles: mapStyles }}
    >






    </GoogleMap>
        
        </>
    )
}

const MapWrapped = withScriptjs(withGoogleMap(Map));


export  function GMAP() {
    return (
        <Flex width="100%" height="100vh" flexDirection="column" mx="auto" my="2" boxShadow="dark-lg">
        <MapWrapped
          googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY`}
          loadingElement={<div style={{ height: `100%` }} />}
          containerElement={<div style={{ height: `100%` }} />}
          mapElement={<div style={{ height: `100%` }} />}
        />
      </Flex>
    );
  }
