import { Divider, Flex, HStack, Text, Box, Link , VStack } from "@chakra-ui/layout"
import { AspectRatio, Image,Button, Tabs, TabList, TabPanels, Tab, TabPanel ,Badge,} from "@chakra-ui/react"
import React, { useEffect,useState } from "react"
import { useParams } from 'react-router'
import { getPoiData , getNearByPlaces} from "../../services/ViewPOIServices"
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import { Heading } from "@chakra-ui/layout";
import { IoLocation } from "react-icons/io5";
import { Tag , TagLabel,} from "@chakra-ui/tag";
import { Avatar } from "@chakra-ui/avatar";
import { StarIcon } from "@chakra-ui/icons"
import { useHistory } from "react-router"


export const ViewPois=()=>{
    const history=useHistory()
    const [data,setData]=useState({})
    const {place_id}=useParams()
    const [location,setLocation]=useState({})
    const [nearByPlaces,setNearByPlaces]=useState([])

    useEffect( ()=>{
            getPoiData(place_id).then((r)=>{
                setData(r)
                setLocation(r.geometry.location)
                // console.log(r)
                
            }); 
            
        },[place_id] )

    useEffect(()=>{
        
        getNearByPlaces(location).then((r)=>{
            setNearByPlaces(r)
        })

    }, [location])
    return(
        <>

        {/* <Box width="full"  p="6" bg="white"  position="fixed" zIndex="8" height="70px">
        <HStack spacing="24px" justifyContent="center" alignItems="center"> 
                <Button colorScheme="red" variant="outline">
                    Button
                </Button>
                <Button colorScheme="red" variant="outline">
                    Button
                </Button>
        </HStack>
        </Box>
        <Box width="full" boxShadow="lg"  p="6" bg="white" height="70px">
        </Box>  */}
        <Tabs isFitted variant="line"  >
        <TabList  position="fixed" width="full" zIndex="8" bg="white" p={2} >
            <Tab >Place Details</Tab>
            <Tab>You may like</Tab>
        </TabList>


        <TabPanels >


            <TabPanel>
        
        

            <PoiCard data={data} />
         
        
          {/* <iframe src="https://github.com/googlemaps/google-maps-services-js/blob/master/e2e/places/details.test.ts" /> */}
         
          </TabPanel>


         <TabPanel>
            {(nearByPlaces) && nearByPlaces.map((nearByPlace,index)=>{
                return (
                    <NearByPlaceCard name={nearByPlace.name} photo={nearByPlace.photos[0].photo_reference} address={nearByPlace.formatted_address} rating={nearByPlace.rating} index={index}  place_id={nearByPlace.place_id} />
                )
            })
            
            
            }
         </TabPanel>


        </TabPanels>

        </Tabs>
        
        
        
        </>
    )
}
const Carousal=({photos=[]})=>{
    const [references, setReferences]=useState([])
    useEffect(()=>{
            setReferences(photos)
    }, [photos])
    return(
        <Carousel>
                
                {
                    references.map((reference)=>{
                        return(
                            <div  >
                            <img src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=800&photo_reference=${reference.photo_reference}&key=AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY`} />
                            
                        </div>
                        )
                    })
                }
                
            </Carousel>
    )
}


const Review=({author_name, author_url,profile_photo_url,relative_time_description,text,rating})=>{
    return(
        <Flex borderWidth="1px" borderRadius="lg" alignItems="start" flexDirection="column" p={5} m={5}>
                    <Link href={author_url} isExternal>
                    <Tag size="lg" colorScheme="red" borderRadius="full">
                    <Avatar
                        src={profile_photo_url}
                        size="xs"
                        name="Segun Adebayo"
                        ml={-1}
                        mr={2}
                    />
                    <TagLabel>{author_name}</TagLabel>
                    </Tag>
                    </Link>

                    <Tag m={2}>{relative_time_description}</Tag>

                    <HStack m={2}>
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <Box>rating {rating}</Box>
                                        
                    </HStack>
                    <Text fontSize="sm">{text}</Text>
            </Flex>
    )
}

const PoiCard=({data={}})=>{
    return(
        <Flex  flexDirection="column" alignItems="center" width="70%" marginX="auto" boxShadow="dark-lg" padding={4} marginTop={2}>            
            

            <HStack><IoLocation/>
            <Heading padding={5}>{data.name}</Heading>
            </HStack>

            <Carousal photos={data.photos} />

            <HStack spacing={6} marginTop="-220" p={2}> 
                {(data.types)  && data.types.map((type)=>{
                    return(
                        <Tag  variant="solid" colorScheme="teal">
                            {type}
                        </Tag>
                    )
                })}
            </HStack>

            <Text fontSize="md">{data.formatted_address}</Text>
            
         




            <Divider  padding="5" />
            
            <Text fontSize="3xl">User reviews</Text>
            
            <>
            {(data.reviews) && data.reviews.map((review)=>{
                return(
                    <Review  author_name={review.author_name} profile_photo_url={review.profile_photo_url} relative_time_description={review.relative_time_description} text={review.text} rating={review.rating} author_url={review.author_url}/>
                )
            })}
            
            
            </>
                       
        </Flex>
    )
}



const NearByPlaceCard=({distance,duration,photo,index,name,address, types=[], rating, place_id})=>{
    const [link,setLink]=useState("")
    const history=useHistory()
    useEffect(() => {
        
      
        setLink(`https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photo_reference=${photo}&key=AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY`)
    }, [photo])



    return(
        <Flex flexDirection="column" alignItems="center" padding="10px">
                      


                        <Flex flexDirection="row" margin={5} borderWidth="1px" borderRadius="lg" p={5} >
                           <Image src={link}  width="400px" height="300px" objectFit="cover" borderRadius="lg"  />
                           <VStack spacing={3.5} alignItems="start" p={5}>
                                 <Avatar name={`${index}`}  />
                                <Box lineHeight="tall" fontWeight="semibold" as="h4"> {name}</Box>
                                <Box color="gray.600" fontSize="sm">{address}</Box>
                                <HStack>
                                        <>    {types.map((item)=>{
                                                return(
                                                    <Badge borderRadius="full" px="2" colorScheme="teal">{item}</Badge>
                                                )
                                            })}
                                        </> 
                                         
                                </HStack>

                                <HStack>
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <Box>rating {rating}</Box>
                                        
                                </HStack>
                                        

                           </VStack>

                        </Flex>


            </Flex>
    )
}
