const {Client} = require("@googlemaps/google-maps-services-js");

export const getPoiData=async(place_id)=>{

    const client = new Client({});
    return client.placeDetails({params:{
        place_id: place_id,
        key: "AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY",

    }}).then((r)=>{
       
        return r.data.result
    }).catch(e=>{
        console.log(e)
    })

}

export const getNearByPlaces=(location)=>{
    const client = new Client({});
    return client.placesNearby({
        params:{
            location:location,
            key: "AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY",
            

