import { Divider, Flex, HStack, Text, Box, Link  } from "@chakra-ui/layout"
import { AspectRatio, Image,Button } from "@chakra-ui/react"
import React, { useEffect,useState } from "react"
import { useParams } from 'react-router'
import { getPoiData } from "../../services/ViewPOIServices"
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import { Heading } from "@chakra-ui/layout";
import { IoLocation } from "react-icons/io5";
import { Tag , TagLabel,} from "@chakra-ui/tag";
import { Avatar } from "@chakra-ui/avatar";
import { StarIcon } from "@chakra-ui/icons"
import { useHistory } from "react-router"


export const ViewPois=()=>{
    const history=useHistory()
    const [data,setData]=useState({})
    const {place_id}=useParams()

    useEffect( ()=>{
            getPoiData(place_id).then((r)=>{
                setData(r)
                console.log(r)
                
            })
            
        },[place_id] )
    return(
        <>

        <Box width="full" boxShadow="xl" p="6" bg="white" height="30px" position="fixed" zIndex="9">
        <HStack spacing="24px" justifyContent="center" alignItems="center"> 
                <Button colorScheme="teal" variant="outline">
                    Button
                </Button>
                <Button colorScheme="teal" variant="outline">
                    Button
                </Button>
        </HStack>
        </Box>
        

        <Flex  flexDirection="column" alignItems="center" width="70%" marginX="auto" boxShadow="dark-lg" padding={4}>


            

            <HStack><IoLocation/>
            <Heading padding={5}>{data.name}</Heading>
            </HStack>

            <Carousal photos={data.photos} />

            <HStack spacing={6} marginTop="-220" p={2}> 
                {(data.types)  && data.types.map((type)=>{
                    return(
                        <Tag  variant="solid" colorScheme="teal">
                            {type}
                        </Tag>
                    )
                })}
            </HStack>

            <Text fontSize="md">{data.formatted_address}</Text>
            
         




            <Divider  padding="5" />
            
            <Text fontSize="3xl">User reviews</Text>
            
            <>
            {(data.reviews) && data.reviews.map((review)=>{
                return(
                    <Review  author_name={review.author_name} profile_photo_url={review.profile_photo_url} relative_time_description={review.relative_time_description} text={review.text} rating={review.rating} author_url={review.author_url}/>
                )
            })}
            
            
            </>
            
            
        </Flex>
         
        
          {/* <iframe src="https://github.com/googlemaps/google-maps-services-js/blob/master/e2e/places/details.test.ts" /> */}
            

        
        
        
        </>
    )
}
const Carousal=({photos=[]})=>{
    const [references, setReferences]=useState([])
    useEffect(()=>{
            setReferences(photos)
    }, [photos])
    return(
        <Carousel>
                
                {
                    references.map((reference)=>{
                        return(
                            <div  >
                            <img src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=800&photo_reference=${reference.photo_reference}&key=AIzaSyCB9FiwGVeEmdfBAwxiQpPuz0fsDMiwPWY`} />
                            
                        </div>
                        )
                    })
                }
                
            </Carousel>
    )
}


const Review=({author_name, author_url,profile_photo_url,relative_time_description,text,rating})=>{
    return(
        <Flex borderWidth="1px" borderRadius="lg" alignItems="start" flexDirection="column" p={5} m={5}>
                    <Link href={author_url} isExternal>
                    <Tag size="lg" colorScheme="red" borderRadius="full">
                    <Avatar
                        src={profile_photo_url}
                        size="xs"
                        name="Segun Adebayo"
                        ml={-1}
                        mr={2}
                    />
                    <TagLabel>{author_name}</TagLabel>
                    </Tag>
                    </Link>

                    <Tag m={2}>{relative_time_description}</Tag>

                    <HStack m={2}>
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <StarIcon color={"teal.500"} />
                                        <Box>rating {rating}</Box>
                                        
                    </HStack>
                    <Text fontSize="sm">{text}</Text>
            </Flex>
    )
}
